-- =============================================
-- Author:           <John Raj>
-- Create date: 12 June 2018
-- Description: Check MET or MISSED TV
-- [dbo].[CheckTVMetOrMissedForRBody]  ('2013-04-03 00:00:00.000','SERVICE','2013-04-04 00:00:00.000','1','H')
-- =============================================
CREATE FUNCTION [dbo].[CheckTVMetOrMissedForRBody] 
(
       -- Add the parameters for the function here
       @dateIn Datetime,
       @tvType nvarchar(30),
       @validateDate Datetime,
	   @category nvarchar(MAX),
       @statusFlag   char(5)      
)
RETURNS VARCHAR(250)
AS

-- by uspLogError in the ErrorLog table BEGIN SET NOCOUNT ON;
-- Output parameter value of 0 indicates that error -- information was not logged SET @errorLogID = 0;
BEGIN TRY -- Return if there is no error information to log IF ERROR_NUMBER() IS NULL RETURN;
-- Return if inside an uncommittable transaction. -- Data insertion/modification is not allowed when -- a transaction is in an uncommittable state. IF XACT_STATE() = -1 BEGIN PRINT 'Cannot log error since the current transaction is in an uncommittable state. ' + 'Rollback the transaction before executing uspLogError in order to successfully log error information.'; RETURN; END
INSERT [dbo].[ErrorLog] ( [UserName], [ErrorNumber], [ErrorSeverity], [ErrorState], [ErrorProcedure], [ErrorLine], [ErrorMessage] ) VALUES ( CONVERT(sysname, CURRENT_USER), ERROR_NUMBER(), ERROR_SEVERITY(), ERROR_STATE(), ERROR_PROCEDURE(), ERROR_LINE(), ERROR_MESSAGE() );
-- Pass back the ErrorLogID of the row inserted SET @errorLogID = @@IDENTITY; END TRY BEGIN CATCH PRINT 'An error occurred in stored procedure uspLogError: '; EXECUTE [dbo].[PrintError]; RETURN -1; END CATCH END;

BEGIN
    SET NOCOUNT ON;

    -- Print error information. 
    PRINT 'Error ' + CONVERT(varchar(50), ERROR_NUMBER()) +
          ', Severity ' + CONVERT(varchar(5), ERROR_SEVERITY()) +
          ', State ' + CONVERT(varchar(5), ERROR_STATE()) + 
          ', Procedure ' + ISNULL(ERROR_PROCEDURE(), '-') + 
          ', Line ' + CONVERT(varchar(5), ERROR_LINE());
    PRINT ERROR_MESSAGE();
END;

